mapper.update:
UPDATE FILETABLE SET FILENAME=#{fileName},UPLOADTIME=#{uploadTime},
DOWNLOADTIMES=#{downLoadTimes},USERROLE=#{userRole},FILEPATH=#{filePath},UPLOADIP=#{uploadIp},
ISDELETED=#{isDeleted}
WHERE ISDELETED=0 AND FILEID=#{fileId}

mapper.delete:
UPDATE FILETABLE SET 
ISDELETED=1
WHERE FILEID=#{id}
